var $bm_rt;
var damp, freq, rebond, tempsClefProx, tempsDebut, tempsDebut, temps, spring;
damp = 3;
freq = 2;
$bm_rt = rebond = 0;
if (numKeys > 1 && freq != 0) {
    if (nearestKey(time).index == 1) {
        $bm_rt = value;
    } else {
        if (length(velocity) == 0) {
            tempsClefProx = nearestKey(time).time;
            if (tempsClefProx <= time) {
                tempsDebut = tempsClefProx;
            } else {
                tempsDebut = key(sub(nearestKey(time).index, 1)).time;
            }
            temps = sub(time, tempsDebut);
            $bm_rt = spring = mul(velocityAtTime(sub(tempsDebut, thisComp.frameDuration)), div(mul(div(0.15, freq), Math.sin(mul(mul(mul(freq, temps), 2), Math.PI))), Math.exp(mul(temps, damp))));
            if (rebond == 0)
                $bm_rt = sum(valueAtTime(tempsDebut), spring);
            if (rebond == 1 && valueAtTime(tempsDebut - thisComp.frameDuration) > valueAtTime(tempsDebut))
                $bm_rt = sum(valueAtTime(tempsDebut), Math.abs(spring));
            if (rebond == 1 && valueAtTime(tempsDebut - thisComp.frameDuration) < valueAtTime(tempsDebut))
                $bm_rt = sub(valueAtTime(tempsDebut), Math.abs(spring));
        } else {
            $bm_rt = value;
        }
    }
} else {
    $bm_rt = value;
}

